import React , {useState, Component} from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';


class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      error : null,
      isLoaded: false,
      status: 0,
      mess: "Press to connect",
      items: []
    }
  }

  componentDidMount(){
    this.fetchData()
  }

  fetchData(){
    fetch("http://localhost:5000/")
    .then(response => response.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result.items,
          status: 200
        });
      },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        })
      }
    )
  }

  getData(){
    if(this.status == 200){  
      return this.state.items[0].data
    }

  }
  onPress = () => {
    this.setState({
      mess : this.state.items[0].data
    })
  }

  render(){

    console.log(this.state)
    const { error, isLoaded, items } = this.state;
    //help

    if (error) {
      return <View><Text>{error.message}</Text></View>
    } else if (!isLoaded) {
      return <View><Text>Loading...</Text></View>
    } return (
      <View style={styles.container}>
        <Text>{this.state.mess}</Text>
        {/* <Button title ="Press Me!" onPress={() => this.state.mess = this.state.items[0].data}/> */}
        <Button title ="Press Me!" onPress={() => this.onPress()}/>
      </View>
    );
  }
}
  const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default App;
